import './App.css';
import Home from './Sections/Home/home';
import { useState,useEffect } from 'react';
import DotLoader from "react-spinners/DotLoader";
function App() {
  const[loading,setLoading] =useState(false)
  useEffect(()=>{
    setLoading(true)
    setTimeout(()=>{
      setLoading(false)
    },2000)
  },[])
  return (
    <div className="App">

      {
        loading ?
        (<DotLoader color={"#004fa1"} loading={loading} size={150}/>)
        :
        (<Home/>)
      }
    </div>

  );
}

export default App;
